import React, {useRef} from 'react'
import { MdFavoriteBorder,MdFavorite, MdStarBorder, MdAddShoppingCart, MdOutlineRemoveShoppingCart } from 'react-icons/md';
import { IconContext } from 'react-icons';

const FoodComp = (props) => {
    const { id, title, img, price } = props.food
    const numberInputRef = useRef()
    const favoriteBorderRef = useRef()
    const favoriteRef = useRef()

    const shoppingHandler = () => {
        numberInputRef.current.classList.remove('hidden')
    }

    const favoriteHandler = (event) => {
        console.log(event);
    }
    return (
        <>
            <div className='food-card border-sm border-gray-300 w-full border rounded-md shadow-md'>
                <div className='food-img flex flex-wrap w-full rounded-md'>
                    <img src={img} className='rounded-t-md' alt="" srcset="" />
                </div>
                <div className='flex items-start w-full gap-3 flex-col p-4'>
                    <p className='food-name font-bold text-xl '>{title}</p>
                    <p className='food-price font-thin text-sm'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsa ex </p>
                    <div className="food-options w-full flex justify-between">
                        <p className='food-price text-primary'>{price} $</p>
                        <div className="food-order flex gap-2">
                            <input ref={numberInputRef} type="number" defaultValue='1' max='5' min='1' className='w-8 pl-1 outline-none  border border-primary border-opacity-40 rounded-sm hidden' />
                            <MdAddShoppingCart className='font-bold text-2xl hover:scale-110 hover:text-primary transition-all ease-in-out' onClick={shoppingHandler} />
                            {/* <MdOutlineRemoveShoppingCart className='font-bold text-2xl hover:scale-125 ' /> */}
                            <MdFavoriteBorder className='font-bold text-2xl hover:scale-110 hover:text-primary transition-all ease-in-out' onClick={favoriteHandler} />
                            <MdFavorite className='font-bold text-2xl hover:scale-110 transition-all ease-in-out text-red-400 hidden'/>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default FoodComp
