import React, { useState, useEffect } from 'react';
import { FaInstagram, FaTwitter, FaWhatsapp, FaFacebookF } from 'react-icons/fa';
import {MdOutlineMenu} from 'react-icons/md';


const Navigation = () => {
  const [isScrolling, setIsScrolling] = useState(false)

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 70) {
        console.log('bg changed');
        setIsScrolling(true)
      } else {
        setIsScrolling(false)
      }
    }

    window.addEventListener('scroll', handleScroll)

    return () => {
      window.removeEventListener("scroll",handleScroll)
    }

  }, [])


  const iconsArray = [
    <FaInstagram className = "w-full h-full" />,
    <FaTwitter  className ="w-full h-full" />,
    <FaWhatsapp  className ="w-full h-full"/>,
    <FaFacebookF className="w-full h-full" />
  ]
  
  return (
    <>
      <nav className={`nav-container px-6 py-1 fixed top-0 right-0 left-0 bg-black transition duration-300 ease-in-out z-50 ${isScrolling ? 'bg-[#171717bb]' : ''}`}>
        <div className="nav-content w-full flex justify-between items-center">
          <div className="nav-logo flex justify-center gap-4">
            <img src="./pictures/logo.png" alt="site-logo" className='w-16 md:w-40' />
            <p className=''>Food Ordering App</p>
          </div>

          <MdOutlineMenu className='text-primary text-4xl'/>

          <div className={`nav-menu hidden md:block`}>
            <ul className='flex items-center gap-24 text-white '>
              <li className='text-sm hover:text-primary hover:cursor-pointer'>HOME</li>
              <li className='text-sm hover:text-primary hover:cursor-pointer'>MENU</li>
              <li className='text-sm hover:text-primary hover:cursor-pointer'>PLACE</li>
              <li className='text-sm hover:text-primary hover:cursor-pointer'>CONTACT</li>
            </ul>
          </div>
          <div className="nav-links gap-5 hidden sm:flex">
            {
              iconsArray.map(icon => (
                <div key={Math.random()} className="social-container w-7 h-7 p-1 bg-primary rounded-full flex justify-center items-center">
                  {icon}
                </div>
              ))
            }
          </div>
        </div>
      </nav>
    </>
  )
}

export default Navigation
