import React, {useState} from 'react'
import {MdAddShoppingCart, MdOutlineRemoveShoppingCart } from 'react-icons/md';
import HeartIcon from './HeartIcon';

const FoodComp = (props) => {
    const {title, img, price } = props.food
    const [isFavorite, setIsFavorite] = useState(false)
    const [isAddClicked, setIsAddClicked] = useState(false)

    const shoppingHandler = () => {
        setIsAddClicked(true)
    }

    const favoriteIconHandler = () => {
        setIsFavorite(prev => prev = !prev)
    }



    return (
        <>
            <div className='food-card border-sm border-gray-300 w-full border rounded-md shadow-md'>
                <div className='food-img flex flex-wrap w-full rounded-md'>
                    <img src={img} className='rounded-t-md' alt="" srcset="" />
                </div>
                <div className='flex items-start w-full gap-3 flex-col p-4'>
                    <p className='food-name font-bold text-xl '>{title}</p>
                    <p className='food-price font-thin text-sm'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsa ex </p>
                    <div className="food-options w-full flex justify-between">
                        <p className='food-price text-primary'>{price} $</p>
                        <div className="food-order flex gap-2">
                                
                            <span className={`flex gap-1 items-center  opacity-80 px-1 rounded-md font text-md hover:scale-110 hover:cursor-pointer transition-all ease-in-out ${isAddClicked? 'bg-green-500' : 'bg-primary'}`} onClick={shoppingHandler}>
                                {isAddClicked ? '' : 'buy'}
                                <MdAddShoppingCart />
                            </span>
                            {isAddClicked && <span onClick={() => setIsAddClicked(false)}  className='flex gap-1 items-center bg-red-600 opacity-80 px-1 rounded-md font text-md hover:scale-110 hover:cursor-pointer transition-all ease-in-out'>
                                <MdOutlineRemoveShoppingCart />
                            </span>}
                            {isAddClicked && <input type="number" defaultValue='1' max='5' min='1' className='w-8 pl-1 outline-none  border border-primary border-opacity-40 rounded-md' />}
                            {/* <MdFavoriteBorder className='font-bold text-2xl hover:scale-110 hover:text-primary transition-all ease-in-out'/> */}
                            {/* <MdFavorite className='font-bold text-2xl hover:scale-110 transition-all ease-in-out'  onClick={favoriteHandler} /> */}
                            <HeartIcon height='1.2rem' onClick={favoriteIconHandler} filled={isFavorite}/>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default FoodComp
